static float
Rgsquarezero(float q, float L, float b)
{
    return (L*b/6.0f) * (1.0f - 1.5f*(b/L) + 1.5f*pow((b/L),2) -
         0.75f*pow((b/L),3)*(1.0f - exp(-2.0f*(L/b))));
}

//
static float
Rgsquareshort(float q, float L, float b)
{
    return AlphaSquare(L/b) * Rgsquarezero(q,L,b);
}

