float
elliptical_crosssection(float q, float a, float b)
{
    float uplim,lolim,Pi,summ,arg,zi,yyy,answer;
    int i,nord=76;

    Pi = 4.0f*atan(1.0f);
    lolim=0.0f;
    uplim=Pi/2.0f;
    summ=0.0f;

    for(i=0;i<nord;i++) {
		zi = ( Gauss76Z[i]*(uplim-lolim) + uplim + lolim )/2.0f;
		arg = q*sqrt(a*a*sin(zi)*sin(zi)+b*b*cos(zi)*cos(zi));
		yyy = pow((2.0f * J1(arg) / arg),2);
		yyy *= Gauss76Wt[i];
		summ += yyy;
    }
    answer = (uplim-lolim)/2.0f*summ;
    answer *= 2.0f/Pi;
    return(answer);

}

float flexible_cylinder_ex_kernel(float q,
          float length,
          float kuhn_length,
          float radius,
          float axis_ratio,
          float sld,
          float solvent_sld)
{

	float Pi,flex,crossSect, cont;

	Pi = 4.0f*atan(1.0f);
	cont = sld - solvent_sld;
	crossSect = elliptical_crosssection(q,radius,(radius*axis_ratio));

	flex = Sk_WR(q,length,kuhn_length);
	flex *= crossSect;
	flex *= Pi*radius*radius*axis_ratio*axis_ratio*length;
	flex *= cont*cont;
	flex *= 1.0e-4f;

	return flex;
}

float Iq(float q,
          float length,
          float kuhn_length,
          float radius,
          float axis_ratio,
          float sld,
          float solvent_sld)
{

	float result = flexible_cylinder_ex_kernel(q,
	                length,
	                kuhn_length,
	                radius,
	                axis_ratio,
	                sld,
	                solvent_sld);

	return result;
}

float Iqxy(float qx, float qy,
            float length,
            float kuhn_length,
            float radius,
            float axis_ratio,
            float sld,
            float solvent_sld)
{
	float q;
	q = sqrt(qx*qx+qy*qy);
	float result = flexible_cylinder_ex_kernel(q,
	                length,
	                kuhn_length,
	                radius,
	                axis_ratio,
	                sld,
	                solvent_sld);

	return result;
}


