    yy = (1.0f - w_WR(q*sqrt(Rg2)))*Sdebye(q,L,b) +
        w_WR(q*sqrt(Rg2))*(C1*pow((q*sqrt(Rg2)),(-1.0f/miu)) +
        C2*pow((q*sqrt(Rg2)),(-2.0f/miu)) +
        C3*pow((q*sqrt(Rg2)),(-3.0f/miu)));

